<js>
result.toArray().slice(0, 3).map(el => {
	var url = String(el.select('a:eq(0)').attr('href')).replace(/^\//, baseUrl.match(/(.+?\/\/[^\/]+\/)/)[1]);
	var J = org.jsoup.Jsoup.parse(java.ajax(url));
	var o = selector => String(J.select(selector).text());
	return {
	  name: o('h1'),
	  author: o('.bq span:eq(1)'),
	  kind: o('.bq span:eq(2)').replace(/.*：|中|已/g, ''),
	  latest: o('.lastchapter a') + ' • ' + o('.bq span:eq(0)').match(/(\d+-\d+-\d+)/)[1],
	  intro: o('.jj').replace(/\s+/g, ''),
	  cover: J.select('.pic img').attr('src'),
		url: url,
	};
}); 
</js>

